select Concat(Concat(a.relname,','),des.description) as Seq_Name from pg_catalog.pg_class a inner join pg_catalog.pg_namespace ns on a.relnamespace = ns.oid left join pg_catalog.pg_description des on a.oid = des.objoid and des.objsubid = 0 inner join pg_catalog.pg_attribute attr on a.oid = attr.attrelid and attr.attnum > 0 left join pg_catalog.pg_description des2 on a.oid = des2.objoid and attr.attnum = des2.objsubid where a.relkind = 'S' and ns.nspname IN ('public') group by a.relname,des.description order by a.relname ;